<?php	if( isset( $_GET['ref'] ) ){		echo '<input type="hidden" id="ref" value="'.$_GET['ref'].'" />';	} else echo '<input type="hidden" id="ref" value="id_jurnal" />';?><h2>Kode Jurnal</h2><div><input type="text" id="key" title="Masukkan nilai untuk mencari data. Klik pada baris yang diinginkan untuk memasukkan nilai" /></div><div id="result"></div><script src='<?php echo base_url('assets/js/unserialize.js'); ?>'></script><script src='<?php echo base_url('assets/js/utf8.decode.js'); ?>'></script><script>$( document ).ready( function() {	$( '#key' ).focus();	var tgl; var bln; var thn; var tanggal;	$.post( '../db_jurnal', { key: '', tipe: 'terima' }, function( result ){		var data = unserialize( result );		var tabel = '<table class="table"><thead><tr><th>Kode Jurnal</th></tr></thead><tbody>';		if( data[ 0 ] == null ){			tabel += '<tr id=""><td colspan="2">Tidak ada data</td></tr>';		} else {			var i = 0;			while( data[ i ] ){				tabel += '<tr class="result" id="'+escape( data[ i ][ 'id_jurnal' ] )+'">';				tabel += '<td>'+data[ i ][ 'id_jurnal' ]+'</td></tr>';				i++;			}		}		tabel += '</tbody></table>';		$( '#result' ).html( tabel ).fadeIn();	});	$( '#key' ).keyup( function( e ){		if ( e.which === 13 ){			$( '#'+$( "#ref" ).val(), window.parent.document ).val( unescape( $( "tbody tr:first-child" ).attr('id') ) );			$( '#'+$( "#ref" ).val(), window.parent.document ).focus();			parent.$.fancybox.close();		} else if ( e.which === 27 ){			parent.$.fancybox.close();		} else {			$( '#result' ).html( '' );			$.post( '../db_jurnal', { key: $('#key').val(), tipe: 'terima' }, function( result ){				var data = unserialize( result );				var tabel = '<table class="table"><thead><tr><th>Kode Jurnal</th></tr></thead><tbody>';				if( data[ 0 ] == null ){					tabel += '<tr id=""><td colspan="2">Tidak ada data</td></tr>';				} else {					var i = 0;					while( data[ i ] ){						tabel += '<tr class="result" id="'+escape( data[ i ][ 'id_jurnal' ] )+'">';						tabel += '<td>'+data[ i ][ 'id_jurnal' ]+'</td></tr>';						i++;					}				}				tabel += '</tbody></table>';				$( '#result' ).html( tabel ).fadeIn();			});		}	});	$( '.result' ).live( 'click', function(){		$( '#'+$( "#ref" ).val(), window.parent.document ).val( unescape( $( this ).attr( 'id' ) ) );		$( '#'+$( "#ref" ).val(), window.parent.document ).focus();		parent.$.fancybox.close();	});});</script>