<?php	if( isset( $_GET['ref'] ) ){		echo '<input type="hidden" id="ref" value="'.$_GET['ref'].'" />';	} else echo '<input type="hidden" id="ref" value="" />';	if( isset( $_GET['jurnal'] ) ){		echo '<input type="hidden" id="jurnal" value="'.$_GET['jurnal'].'" />';	} else echo '<input type="hidden" id="jurnal" value="" />';?><h2>No Transaksi</h2><div><input type="text" id="key" title="Masukkan nilai untuk mencari data. Klik pada baris yang diinginkan untuk memasukkan nilai" /></div><div id="result"></div><script src='<?php echo base_url('assets/js/unserialize.js'); ?>'></script><script src='<?php echo base_url('assets/js/utf8.decode.js'); ?>'></script><script>$( document ).ready( function() {	$( '#key' ).focus();	var tgl; var bln; var thn; var tanggal;	$.post( '../db_transaksi', { jurnal: $('#jurnal').val(), key: '', tipe: 'terima' }, function( result ){		var data = unserialize( result );		var tabel = '<table class="table"><thead><tr><th>No Transaksi</th><th>Tanggal</th></tr></thead><tbody>';		if( data[ 0 ] == null ){			tabel += '<tr id=""><td colspan="3">Tidak ada data</td></tr>';		} else {			var i = 0;			while( data[ i ] ){				tabel += '<tr class="result" id="'+escape( data[ i ][ 'id_transaksi' ] )+'">';				tabel += '<td>'+data[ i ][ 'id_transaksi' ]+'</td>';				tanggal = data[ i ][ 'tanggal' ].split( "-" );				tgl = tanggal[ 2 ];				bln = tanggal[ 1 ];				thn = tanggal[ 0 ];				tabel += '<td>'+tgl + "-" + bln + "-" + thn+'</td></tr>';				i++;			}		}		tabel += '</tbody></table>';		$( '#result' ).html( tabel ).fadeIn();	});	$( '#key' ).keyup( function( e ){		if ( e.which === 13 ){			$( '#'+$( "#ref" ).val(), window.parent.document ).val( unescape( $( "tbody tr:first-child" ).attr('id') ) );			$( '#'+$( "#ref" ).val(), window.parent.document ).focus();			parent.$.fancybox.close();		} else if ( e.which === 27 ){			parent.$.fancybox.close();		} else {			$( '#result' ).html( '' );			$.post( '../db_transaksi', { jurnal: $('#jurnal').val(), key: $('#key').val(), tipe: 'terima' }, function( result ){				var data = unserialize( result );				var tabel = '<table class="table"><thead><tr><th>No Transaksi</th><th>Tanggal</th></tr></thead><tbody>';				if( data[ 0 ] == null ){					tabel += '<tr id=""><td colspan="3">Tidak ada data</td></tr>';				} else {					var i = 0;					while( data[ i ] ){						tabel += '<tr class="result" id="'+escape( data[ i ][ 'id_transaksi' ] )+'">';						tabel += '<td>'+data[ i ][ 'id_transaksi' ]+'</td>';						tanggal = data[ i ][ 'tanggal' ].split( "-" );						tgl = tanggal[ 2 ];						bln = tanggal[ 1 ];						thn = tanggal[ 0 ];						tabel += '<td>'+tgl + "-" + bln + "-" + thn+'</td></tr>';						i++;					}				}				tabel += '</tbody></table>';				$( '#result' ).html( tabel ).fadeIn();			});		}	});	$( '.result' ).live( 'click', function(){		$( '#'+$( "#ref" ).val(), window.parent.document ).val( unescape( $( this ).attr( 'id' ) ) );		$( '#'+$( "#ref" ).val(), window.parent.document ).focus();		parent.$.fancybox.close();	});});</script>